import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [uploadedFilename, setUploadedFilename] = useState("");
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const chatEndRef = useRef(null);

  const handleFileChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!pdfFile) return;

    const formData = new FormData();
    formData.append("file", pdfFile);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData);
      setUploadMessage("‚úÖ Uploaded successfully!");
      setUploadedFilename(response.data.filename);
    } catch (error) {
      console.error("‚ùå Upload error:", error);
      setUploadMessage("Upload failed.");
    }
  };

  const handleAsk = async () => {
    if (!question || !uploadedFilename) return;

    const userMessage = { sender: "user", text: question };
    setMessages((prev) => [...prev, userMessage]);
    setIsTyping(true);
    setQuestion("");

    const formData = new FormData();
    formData.append("filename", uploadedFilename);
    formData.append("question", userMessage.text);

    try {
      const response = await axios.post("http://localhost:8000/ask/", formData);
      const botMessage = { sender: "bot", text: response.data.answer };
      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = { sender: "bot", text: "Error getting answer." };
      setMessages((prev) => [...prev, errorMessage]);
    }
    setIsTyping(false);
  };

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, isTyping]);

  return (
    <div className="chat-app">
      <div className="chat-header">
        Lexa üß† PDF Q&A Assistant
        <div className="upload-section">
          <input type="file" id="fileInput" onChange={handleFileChange} hidden />
          <label htmlFor="fileInput" className="upload-btn">üìÑ Choose PDF</label>
          <button onClick={handleUpload} className="upload-btn">‚¨ÜÔ∏è Upload</button>
        </div>
        {uploadMessage && <p>{uploadMessage}</p>}
      </div>

      <div className="chat-body">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        {isTyping && <div className="message bot typing">Lexa is thinking...</div>}
        <div ref={chatEndRef}></div>
      </div>

      <div className="chat-footer">
        <input
          type="text"
          placeholder="Ask something from the PDF..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button onClick={handleAsk}>Send</button>
      </div>
    </div>
  );
}

export default App;
