import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [question, setQuestion] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [uploadedFilename, setUploadedFilename] = useState("");

  const handleFileChange = (e) => setPdfFile(e.target.files[0]);

  const handleUpload = async () => {
    if (!pdfFile) {
      setUploadMessage("⚠️ Please select a PDF file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", pdfFile);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData);
      setUploadMessage("✅ PDF uploaded successfully!");
      setUploadedFilename(response.data.filename);
    } catch (error) {
      setUploadMessage("❌ Upload failed.");
    }
  };

  const handleAsk = async () => {
    if (!question.trim() || !uploadedFilename) return;

    const userMsg = { type: "user", text: question };
    setChatHistory((prev) => [...prev, userMsg]);

    const formData = new FormData();
    formData.append("filename", uploadedFilename);
    formData.append("question", question);

    setQuestion("");

    try {
      const response = await axios.post("http://localhost:8000/ask/", formData);
      const botMsg = { type: "bot", text: response.data.answer };
      setChatHistory((prev) => [...prev, botMsg]);
    } catch (error) {
      const errMsg = { type: "bot", text: "❌ Error retrieving answer." };
      setChatHistory((prev) => [...prev, errMsg]);
    }
  };

  return (
    <div className="chat-app">
  <div className="chat-header">
    <h1>Lexa 🧠 PDF Q&A Assistant</h1>
    <div className="upload-section">
      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload}>📄 Upload</button>
    </div>
    <div className="upload-msg">{uploadMessage}</div>
  </div>

  <div className="chat-body">
    {/* loop through Q&A here if needed */}
  </div>

  <div className="chat-footer">
    <input
      type="text"
      placeholder="Ask something from the PDF..."
      value={question}
      onChange={(e) => setQuestion(e.target.value)}
    />
    <button onClick={handleAsk}>Send</button>
  </div>
</div>

  );
}

export default App;
