import React, { useState } from "react";
import axios from "axios";

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [uploadedFilename, setUploadedFilename] = useState("");

  const handleFileChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!pdfFile) {
      setUploadMessage("Please select a PDF file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", pdfFile);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData);
      setUploadMessage("‚úÖ Uploaded successfully!");
      setUploadedFilename(response.data.filename);
      console.log("Upload response:", response.data);
    } catch (error) {
      console.error("‚ùå Upload error:", error);
      setUploadMessage("Upload failed.");
    }
  };

  const handleAsk = async () => {
    if (!question || !uploadedFilename) {
      setAnswer("Please upload a PDF and type a question.");
      return;
    }

    const formData = new FormData();
    formData.append("filename", uploadedFilename);
    formData.append("question", question);

    try {
      const response = await axios.post("http://localhost:8000/ask/", formData);
      setAnswer(response.data.answer);
    } catch (error) {
      console.error("‚ùå Ask error:", error);
      setAnswer("Error getting answer.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>üìÑ Upload PDF & Ask Questions</h2>

      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload PDF</button>
      <p>{uploadMessage}</p>

      <hr />

      <textarea
        placeholder="Type your question..."
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        rows={3}
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <button onClick={handleAsk}>Ask</button>

      <h3>Answer:</h3>
      <p>{answer}</p>
    </div>
  );
}

export default App;
