import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [uploadMessage, setUploadMessage] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [uploadedFilename, setUploadedFilename] = useState("");

  const handleFileChange = (e) => setPdfFile(e.target.files[0]);

  const handleUpload = async () => {
    if (!pdfFile) {
      setUploadMessage("‚ö†Ô∏è Please select a PDF first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", pdfFile);

    try {
      const response = await axios.post("http://localhost:8000/upload/", formData);
      setUploadMessage("‚úÖ Uploaded successfully!");
      setUploadedFilename(response.data.filename);
    } catch (error) {
      console.error("‚ùå Upload error:", error);
      setUploadMessage("‚ùå Upload failed.");
    }
  };

  const handleAsk = async () => {
    if (!question || !uploadedFilename) {
      setAnswer("‚ö†Ô∏è Upload a PDF and enter a question.");
      return;
    }

    const formData = new FormData();
    formData.append("filename", uploadedFilename);
    formData.append("question", question);

    try {
      const response = await axios.post("http://localhost:8000/ask/", formData);
      setAnswer(response.data.answer);
    } catch (error) {
      console.error("‚ùå Ask error:", error);
      setAnswer("‚ùå Error retrieving answer.");
    }
  };

  return (
    <div className="app-wrapper">
      <div className="app-container animate-fade-in">
        <h1>üìò Lexa ‚Äì PDF Q&A Assistant</h1>
        <p className="subtitle">Upload a PDF and ask questions in natural language.</p>

        <div className="upload-section">
          <input type="file" accept=".pdf" onChange={handleFileChange} />
          <button onClick={handleUpload}>üì§ Upload</button>
          <p className="message">{uploadMessage}</p>
        </div>

        <hr />

        <div className="question-section">
          <textarea
            placeholder="üí¨ Type your question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            rows={3}
          />
          <button onClick={handleAsk}>üîç Ask</button>
        </div>

        <div className="answer-box">
          <h3>üìù Answer:</h3>
          <p>{answer}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
